// Util function to convert a DOM rect object to a plain object
function rectToPlainObject(rect) {
  return {
    width: rect.width,
    height: rect.height,
    top: rect.top,
    right: rect.right,
    bottom: rect.bottom,
    left: rect.left,
    x: rect.left,
    y: rect.top,
  };
}

// Main function to get the bounding client rect of an element
export default function getBoundingClientRect(element) {
  return rectToPlainObject(element.getBoundingClientRect());
}
