import { Placement } from "../enums.js"; // assuming the enum is exported from the module

/**
 * Returns the base placement value by removing any modifiers from the input string.
 * @param {Placement|string} placement - The placement value to extract the base value from.
 * @returns {Placement} The base placement value.
 */
export function getBasePlacement(placement) {
  // Check if the input is a string or a value from the Placement enum
  if (typeof placement === 'string') {
    placement = placement.toLowerCase().trim(); // convert to lowercase and trim whitespace
  } else if (typeof placement !== 'number') {
    throw new Error('Invalid placement value. Expected a string or a value from the Placement enum.');
  }

  // Extract the base placement value by splitting on the hyphen and returning the first part
  return Placement[placement.split('-')[0]];
}
