export default function getMainAxisFromPlacement(placement) {
  return ['top', 'bottom'].includes(placement) ? 'x' : 'y';
}


export default function getMainAxisFromPlacement(placement) {
  if (typeof placement !== 'string') {
    throw new Error('Expected a string as the placement argument');
  }

  return ['top', 'bottom'].includes(placement) ? 'x' : 'y';
}
